mapper.delete:

delete from KUHDBA.COMTSMSDH
where SENDID = #{SENDID,jdbcType=VARCHAR}
  and INDATE = #{INDATE,jdbcType=VARCHAR}
  and INTIME = #{INTIME,jdbcType=VARCHAR}
  and C_SEQNO = #{c_SEQNO,jdbcType=DECIMAL}

mapper.select:

select SENDID, INDATE, INTIME, C_SEQNO, MEMBER, SPHONE1, SPHONE2, SPHONE3, SENDNAME,
RPHONE1, RPHONE2, RPHONE3, RECVNAME, MSG, URL, RDATE, RTIME, RESULT, KIND, ERRCODE,
C_RESULT, C_PID, C_SENDTIME, C_MSGID, C_MSGPARAM, SEQNO, C_USERID, SYSINFO, RECVTIME,
MEDIATYPE, TEMPLET
from KUHDBA.COMTSMSDH
where SENDID = #{SENDID,jdbcType=VARCHAR}
  and INDATE = #{INDATE,jdbcType=VARCHAR}
  and INTIME = #{INTIME,jdbcType=VARCHAR}
  and C_SEQNO = #{c_SEQNO,jdbcType=DECIMAL}
